import React, { useState } from "react";
import "./Weathers.css";
import axios from "axios";

export default function Weather() {
  let [City, SetCity] = useState("");
  let [temprature, setTemprature] = useState("");
  let [SubmittedCity , setSubmittedCity ] = useState("Tehran");
  const [style , setStyle] = useState("notActivated")
  function ChangeStyle(){
    setStyle("activated");
  }
  function ShowForecast(response) {
    console.log(response);
  }
  function ShowResult(response) {
    setSubmittedCity(City.target.value)
    console.log(response);
    setTemprature(
      <div>
        <li>Tempreture : {Math.round(response.data.main.temp)}°C </li>
        <li>Humidity : {response.data.main.humidity}%</li>
        <li>Wind : {Math.round(response.data.wind.speed)} Km/hr</li>
        <li>Feels like : {Math.round(response.data.main.feels_like)}°C </li>
      </div>
    );
  }
  function getResult(event) {
    event.preventDefault();

    let apiKey = "5122c73584c58f76c2670a7ce4d20f3a";
    console.log(City.target.value);
    axios
      .get(
        `https://api.openweathermap.org/data/2.5/weather?q=${City.target.value}&appid=${apiKey}&units=metric`
      )
      .then(ShowResult);
    axios
      .get(
        `https://api.shecodes.io/weather/v1/forecast?query=${City.target.value}&key=d622ab03edofbbtc80f362a442d6777c&units=metric`
      )
      .then(ShowForecast);
  }
  return (
    <div>
      <div className="Header">
        <span className="Location">
          <i class="fa-sharp fa-solid fa-location-dot"></i> {SubmittedCity}
        </span>
        <form onSubmit={getResult}>
          <input
          className="searchBar"
            type="search"
            placeholder="Search City..."
            onChange={SetCity}
          />
        </form>
      </div>
      <div>
        <ul className="dayButtons">
          <a onClick={ChangeStyle}><li className={style} >Today</li></a>
          <a onClick={ChangeStyle}><li className={style}>Tomorrow</li></a>

        </ul>
      </div>

      {temprature}
    </div>
  );
}
